      <>
    <div className={styles.container}>
      <table className= {styles.sudoku}>
        <tbody>
          {partialSudoku.map((row,rowIndex) => <Sudokurow  key= {rowIndex} row={row} function={handleInput} />)}
        </tbody>
      </table>
      </div>
    <button onClick= {()=>checkSolution(checkedSudoku, sudokuArray.current)}>Send your solution</button>
    <h1>{message}</h1>
